    }
    .image {
      bottom: 10%;
    }
  }
  .outer-container {
    .nav-bar {
      margin-left: 75%;
      ul {
        a {
          color: #000;
          &:hover {
            color: rgba(0, 0, 0, 0.378);
            font-size: 1.2rem;
            border-bottom: 1px rgba(0, 0, 0, 0.378) solid;
