  }

  .outer-container {
    .nav-bar {
      margin-left: 15%;
      margin-top: 20%;
      ul {
        li {
          display: inline;
          border-bottom: 0.5px rgba(0, 0, 0, 0.378) solid;
        }
        a {
          color: #000;
          padding: 0 5px 0 10px;
          // margin-right: 10px;

          // border-bottom: 0.5px rgba(0, 0, 0, 0.378) solid;

          &:hover {
            color: rgba(0, 0, 0, 0.378);
            font-size: 1.2rem;
            border-bottom: 1px rgba(0, 0, 0, 0.378) solid;
          }
        }
      }
    }
