$base: #d6d6d6;
$basePink: #fc4b548c;

body {
  margin: 0;
  padding: 0;
  letter-spacing: 0.2em;
}
.light-overlay {
  background: #f4f4f421;
  position: absolute;
  height: 100vh;
  width: 100%;
  z-index: 10;
}
.outer-container {
  height: 100%;
  width: 100%;
  position: absolute;
  background-color: $base;
  // color: rgba(0, 0, 0, 0.7);
  .nav-bar {
    position: absolute;
    margin-left: 85%;
    display: flex;
    margin-top: 9.5%;
    line-height: 1.5rem;
    font-weight: 600;
    font-family: "Roboto", sans-serif;
    z-index: 10;
    ul {
      text-transform: uppercase;
      line-height: 1.5rem;
      padding: 0;
      margin: 0;
      list-style: none;
      text-decoration: none;
      a {
        text-decoration: none;
        color: rgba(0, 0, 0, 0.678);
        &:hover {
          color: $basePink;
          font-size: 1.2rem;
          border-bottom: 1px $basePink solid;
        }
      }
    }
  }
  .main-text {
    text-transform: uppercase;
    position: absolute;
    z-index: 5;
    top: 28%;
    font-weight: 700;

    h1 {
      font-size: 12vw;
      margin-left: 15%;
      margin-bottom: 0;
    }
    .btm-text {
      font-size: 12vw;
      margin-left: 20%;
      writing-mode: vertical-rl;
    }
  }
}

.inner-container {
  z-index: 1;
  position: absolute;
  background-color: $basePink;
  height: 500px;
  width: 1000px;
  // width: ;
  margin: 15% 16% 0 16%;
  font-weight: 600;
  font-family: "Montserrat", sans-serif;

  .civil-text {
    float: right;
    padding: 1em;
    font-size: 0.8rem;
    // color: rgba(0, 0, 0, 0.678);
  }
  .vert-text {
    font-size: 0.8rem;
    // color: rgba(0, 0, 0, 0.678);
    writing-mode: vertical-rl;
    text-orientation: mixed;
    padding: 1em;
  }
}

.image {
  position: absolute;
  filter: brightness(50%);
  bottom: 15%;
  margin-left: 15%;
}
// ------------------------------------------------

#history {
  height: 100vh;
  margin-top: 50%;
  // width: 100%;
  position: absolute;
}

.gridArea {
  text-align: center;
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(333.33px, 1fr));
  grid-gap: 20px;
  align-items: stretch;
  margin: 2% 5% 0 5%;
}

.card {
  background-color: #f4f4f4;
  border-radius: 5px;
  .grayImg {
    -webkit-filter: grayscale(100%); /* Safari 6.0 - 9.0 */
    filter: grayscale(100%);
    opacity: 0.6;
  }
}
.text {
  font-family: "Roboto", sans-serif;

  h3 {
    font-size: 9vw;
    font-weight: 900;
    letter-spacing: 15px;
    color: $basePink;
    position: absolute;

    writing-mode: vertical-rl;
  }
  h4 {
    text-transform: uppercase;
    color: rgba(0, 0, 0, 0.678);
    margin-top: 0px;
  }
  .topImg {
    top: 5%;
    // margin-left: 5%;
  }
  .btmText {
    top: 70%;
  }
}

// tablet 1200px
@media screen and (max-width: 1200px) {
  .light-overlay {
    height: 100vh;
  }
  .inner-container {
    height: 90vh;
    width: 90%;
    margin: 10% 0 10% 3%;
    img {
      width: 108%;
      height: 100%;
